import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Actions, createEffect, ofType } from "@ngrx/effects";
import { FaildAction, LOAD, LoadTodosAction, SuccessAction } from "../actions/todos.action";
import { mergeMap, map, catchError } from "rxjs/operators";
import { of } from "rxjs";

@Injectable()
export class TodosEffect{

    todo$ = createEffect(() => this.actions.pipe(
        ofType(LOAD),
        mergeMap(()=>this.http.get('https://jsonplaceholder.typicode.com/todos').pipe(
            map((data)=> new LoadTodosAction(data)),
            catchError((err) => of(new FaildAction(err)))
        ))
    ))

    constructor(private http:HttpClient, private actions: Actions){

    }
}
