import { FAILED, LOAD, SUCCESS } from "../actions/todos.action"

export interface ToDo{
    userId:number,
    id: number,
    title: string,
    completed: boolean
}

export function TodosReducer(state: ToDo[]|undefined, action:any){
    switch(action.type){
        case SUCCESS:
            console.log("bghitk ", action.payload)
            return action.payload
        // case LOAD:
        //     return {...state}
        case FAILED:
            return state
    }
}
